        int[] ventasDiarias = new int[31];           // 01 VENTAS-DIARIAS OCCURS 31 TIMES PIC 9(7)

        // Inicialización
        for (int i = 0; i < ventasDiarias.length; i++) {
            ventasDiarias[i] = 0;
