package com.empresa.repositorio;

import com.empresa.modelo.Empleado;
import java.util.List;
import java.util.Optional;

public interface EmpleadoRepositorio {
    void guardar(Empleado empleado);
    Optional<Empleado> buscarPorNumero(int numero);
    List<Empleado> buscarTodos();
    List<Empleado> buscarPorDepartamento(String departamento);
    void actualizar(Empleado empleado);
    void eliminar(int numero);
}

// 3. Implementación del repositorio
package com.empresa.repositorio;

import com.empresa.modelo.Empleado;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.*;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.time.LocalDate;
import java.time.format.DateTimeFormatter;
import java.util.*;
import java.util.stream.Collectors;

public class EmpleadoRepositorioArchivo implements EmpleadoRepositorio {
    private static final Logger logger = LoggerFactory.getLogger(EmpleadoRepositorioArchivo.class);
    private static final String ARCHIVO = "empleados.csv";
    private static final DateTimeFormatter FORMATO_FECHA = DateTimeFormatter.ofPattern("yyyy-MM-dd");

    @Override
    public void guardar(Empleado empleado) {
        try (BufferedWriter bw = new BufferedWriter(new FileWriter(ARCHIVO, true))) {
            String linea = String.format("%d,%s,%s,%s,%.2f,%s,%s,%b",
                empleado.getNumeroEmpleado(),
                empleado.getNombre(),
                empleado.getDepartamento(),
                empleado.getPuesto(),
                empleado.getSalario(),
                empleado.getFechaContratacion().format(FORMATO_FECHA),
                empleado.getAntiguedad(),
                empleado.isActivo()
            );
            bw.write(linea);
            bw.newLine();
            logger.info("Empleado {} guardado exitosamente", empleado.getNumeroEmpleado());
        } catch (IOException e) {
            logger.error("Error guardando empleado", e);
            throw new RuntimeException("No se pudo guardar el empleado", e);
        }
    }

    @Override
    public Optional<Empleado> buscarPorNumero(int numero) {
        return buscarTodos().stream()
            .filter(emp -> emp.getNumeroEmpleado() == numero)
            .findFirst();
    }

    @Override
    public List<Empleado> buscarTodos() {
        List<Empleado> empleados = new ArrayList<>();

        try (BufferedReader br = new BufferedReader(new FileReader(ARCHIVO))) {
            String linea;
            while ((linea = br.readLine()) != null) {
                try {
                    Empleado emp = parsearEmpleado(linea);
                    empleados.add(emp);
                } catch (Exception e) {
                    logger.warn("Error parseando línea: {}", linea, e);
                }
            }
        } catch (IOException e) {
            logger.error("Error leyendo archivo", e);
        }

        return empleados;
    }

    @Override
    public List<Empleado> buscarPorDepartamento(String departamento) {
        return buscarTodos().stream()
            .filter(emp -> emp.getDepartamento().equalsIgnoreCase(departamento))
            .collect(Collectors.toList());
    }

    @Override
    public void actualizar(Empleado empleadoActualizado) {
        List<Empleado> todos = buscarTodos();

        for (int i = 0; i < todos.size(); i++) {
            if (todos.get(i).getNumeroEmpleado() == empleadoActualizado.getNumeroEmpleado()) {
                todos.set(i, empleadoActualizado);
                break;
            }
        }

        reescribirArchivo(todos);
        logger.info("Empleado {} actualizado", empleadoActualizado.getNumeroEmpleado());
    }

    @Override
    public void eliminar(int numero) {
        List<Empleado> filtrados = buscarTodos().stream()
            .filter(emp -> emp.getNumeroEmpleado() != numero)
            .collect(Collectors.toList());

        reescribirArchivo(filtrados);
        logger.info("Empleado {} eliminado", numero);
    }

    private Empleado parsearEmpleado(String linea) {
        String[] campos = linea.split(",");

        int numero = Integer.parseInt(campos[0].trim());
        String nombre = campos[1].trim();
        String departamento = campos[2].trim();
        String puesto = campos[3].trim();
        double salario = Double.parseDouble(campos[4].trim());
        LocalDate fecha = LocalDate.parse(campos[5].trim(), FORMATO_FECHA);

        return new Empleado(numero, nombre, departamento, salario, fecha, puesto);
    }

    private void reescribirArchivo(List<Empleado> empleados) {
        try (BufferedWriter bw = new BufferedWriter(new FileWriter(ARCHIVO))) {
            for (Empleado emp : empleados) {
                String linea = String.format("%d,%s,%s,%s,%.2f,%s,%s,%b",
                    emp.getNumeroEmpleado(),
                    emp.getNombre(),
                    emp.getDepartamento(),
                    emp.getPuesto(),
                    emp.getSalario(),
                    emp.getFechaContratacion().format(FORMATO_FECHA),
                    emp.getAntiguedad(),
                    emp.isActivo()
                );
                bw.write(linea);
                bw.newLine();
            }
        } catch (IOException e) {
            logger.error("Error reescribiendo archivo", e);
            throw new RuntimeException("No se pudo actualizar el archivo", e);
        }
    }
}

