package com.empresa.servicio;

import com.empresa.modelo.Empleado;
import com.empresa.repositorio.EmpleadoRepositorio;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.time.LocalDate;
import java.util.*;
import java.util.stream.Collectors;

public class EmpleadoServicio {
    private static final Logger logger = LoggerFactory.getLogger(EmpleadoServicio.class);
    private final EmpleadoRepositorio repositorio;

    public EmpleadoServicio(EmpleadoRepositorio repositorio) {
        this.repositorio = repositorio;
    }

    public void contratarEmpleado(Empleado empleado) {
        Optional<Empleado> existente = repositorio.buscarPorNumero(empleado.getNumeroEmpleado());

        if (existente.isPresent()) {
            throw new IllegalArgumentException("Ya existe empleado con número " + empleado.getNumeroEmpleado());
        }

        repositorio.guardar(empleado);
        logger.info("Nuevo empleado contratado: {}", empleado.getNombre());
    }

    public void despedirEmpleado(int numeroEmpleado) {
        Empleado empleado = repositorio.buscarPorNumero(numeroEmpleado)
            .orElseThrow(() -> new RuntimeException("Empleado no encontrado: " + numeroEmpleado));

        empleado.desactivar();
        repositorio.actualizar(empleado);
        logger.info("Empleado {} despedido", numeroEmpleado);
    }

    public void aumentarSalario(int numeroEmpleado, double porcentaje) {
        Empleado empleado = repositorio.buscarPorNumero(numeroEmpleado)
            .orElseThrow(() -> new RuntimeException("Empleado no encontrado: " + numeroEmpleado));

        double nuevoSalario = empleado.getSalario() * (1 + porcentaje / 100);
        empleado.setSalario(nuevoSalario);
        repositorio.actualizar(empleado);

        logger.info("Salario de {} aumentado {}%: ${} -> ${}", 
                   empleado.getNombre(), porcentaje, 
                   empleado.getSalario() / (1 + porcentaje / 100), 
                   nuevoSalario);
    }

    public List<Empleado> obtenerEmpleadosPorDepartamento(String departamento) {
        return repositorio.buscarPorDepartamento(departamento);
    }

    public Map<String, Double> obtenerTotalSalariosPorDepartamento() {
        return repositorio.buscarTodos().stream()
            .collect(Collectors.groupingBy(
                Empleado::getDepartamento,
                Collectors.summingDouble(Empleado::getSalario)
            ));
    }

    public List<Empleado> obtenerEmpleadosConMasAntiguedad(int años) {
        return repositorio.buscarTodos().stream()
            .filter(emp -> emp.getAntiguedad() >= años)
            .sorted(Comparator.comparingInt(Empleado::getAntiguedad).reversed())
            .collect(Collectors.toList());
    }

    public Map<String, List<Empleado>> obtenerReportePorDepartamento() {
        return repositorio.buscarTodos().stream()
            .collect(Collectors.groupingBy(Empleado::getDepartamento));
    }

    public double calcularNominaTotal() {
        return repositorio.buscarTodos().stream()
            .mapToDouble(Empleado::getSalario)
            .sum();
    }

    public double calcularPromedioSalarios() {
        return repositorio.buscarTodos().stream()
            .mapToDouble(Empleado::getSalario)
            .average()
            .orElse(0.0);
    }
}

